// üéØ Twitter Thread Generator
const transcription = items[0].json.transcription.text;
const title = items[0].json.title;

// Split in tweet-sized chunks
const sentences = transcription.split(/[.!?]+/);
const tweets = [];
let currentTweet = "";
const maxLength = 250; // Leave room for thread numbers

sentences.forEach(sentence => {
  const cleanSentence = sentence.trim();
  if (!cleanSentence) return;

  if ((currentTweet + " " + cleanSentence).length < maxLength) {
    currentTweet += (currentTweet ? " " : "") + cleanSentence + ".";
  } else {
    if (currentTweet) tweets.push(currentTweet);
    currentTweet = cleanSentence + ".";
  }
});

if (currentTweet) tweets.push(currentTweet);

// Limit to max 10 tweets
tweets.splice(10);

// Format as thread
const thread = tweets.map((tweet, index) => {
  const threadNumber = `${index + 1}/${tweets.length}`;
  let formattedTweet = tweet;

  if (index === 0) {
    formattedTweet = `üßµ ${title}\n\n${tweet}`;
  }

  if (index === tweets.length - 1) {
    formattedTweet += "\n\nüîó Video: [Link]\n#AI #Content #Thread";
  }

  return {
    tweet_number: index + 1,
    total_tweets: tweets.length,
    content: formattedTweet,
    hashtags: index === 0 ? "#Thread #AI #Content" : "",
    character_count: formattedTweet.length
  };
});

return [{ json: { thread, total_tweets: tweets.length, platform: "twitter" } }];

// üíº LinkedIn Post Generator
const transcription = items[0].json.transcription.text;
const title = items[0].json.title;
const duration = Math.round(items[0].json.duration / 60);

// Create professional LinkedIn post
const hook = "üéØ Just watched this insightful video and here are my key takeaways:";
const summary = transcription.substring(0, 800);
const cta = "\n\nüí≠ What are your thoughts on this topic?\n\nüîó Watch the full video: [Link]";

const linkedinPost = {
  platform: "linkedin",
  hook: hook,
  content: `${hook}\n\nüì∫ "${title}" (${duration} min)\n\n${summary}...${cta}`,
  hashtags: "#ProfessionalDevelopment #Learning #Insights #VideoContent",
  character_count: (hook + summary + cta).length,
  engagement_elements: {
    question: "What are your thoughts on this topic?",
    cta: "Watch the full video",
    emoji_count: 4
  }
};

return [{ json: linkedinPost }];

// üì∏ Instagram Content Generator
const transcription = items[0].json.transcription.text;
const title = items[0].json.title;

// Split into Instagram-friendly chunks for carousel
const sentences = transcription.split(/[.!?]+/);
const slides = [];
let currentSlide = "";
const maxPerSlide = 150;

// Create 5-8 slides max
for (let i = 0; i < sentences.length && slides.length < 8; i++) {
  const sentence = sentences[i].trim();
  if (!sentence) continue;

  if ((currentSlide + " " + sentence).length < maxPerSlide) {
    currentSlide += (currentSlide ? " " : "") + sentence + ".";
  } else {
    if (currentSlide) slides.push(currentSlide);
    currentSlide = sentence + ".";
  }
}

if (currentSlide && slides.length < 8) slides.push(currentSlide);

// Create Instagram post
const instagramContent = {
  platform: "instagram",
  caption: `üé¨ ${title}\n\n‚ú® Key insights from this amazing video! Swipe to read all takeaways üëâ\n\n${transcription.substring(0, 300)}...\n\n#contentcreator #learning #insights #videomarketing #ai`,
  carousel_slides: slides.map((slide, index) => ({
    slide_number: index + 1,
    content: slide,
    design_note: index === 0 ? "Title slide with video thumbnail" : "Text slide with key point"
  })),
  hashtags: "#contentcreator #learning #insights #videomarketing #ai #education #tips",
  story_version: transcription.substring(0, 200) + "... Swipe up for full video! üîó"
};

return [{ json: instagramContent }];

// üéµ TikTok Script Generator
const transcription = items[0].json.transcription.text;
const title = items[0].json.title;

// Find the most engaging 30-60 second segments
const sentences = transcription.split(/[.!?]+/);
const hooks = [];
const payoffs = [];

// Find hook sentences (questions, surprising statements)
sentences.forEach(sentence => {
  const lower = sentence.toLowerCase().trim();
  if (lower.includes('?') || lower.includes('amazing') || lower.includes('surprising') || lower.includes('secret')) {
    hooks.push(sentence.trim());
  }
  if (lower.includes('solution') || lower.includes('answer') || lower.includes('result')) {
    payoffs.push(sentence.trim());
  }
});

// Create 3 different TikTok scripts
const scripts = [];

// Script 1: Hook + Quick Tips
scripts.push({
  script_number: 1,
  hook: hooks[0] || "You won't believe what I just learned!",
  content: transcription.substring(0, 400),
  cta: "Follow for more tips like this! üî•",
  duration: "30-45 seconds",
  style: "Quick tips format"
});

// Script 2: Problem + Solution
scripts.push({
  script_number: 2,
  hook: "Here's the problem everyone faces...",
  content: transcription.substring(200, 600),
  cta: "Save this for later! üíæ",
  duration: "45-60 seconds",
  style: "Problem/solution format"
});

// Script 3: Surprising fact
scripts.push({
  script_number: 3,
  hook: "This fact will blow your mind! ü§Ø",
  content: transcription.substring(400, 800),
  cta: "Share if this helped you! ‚ù§Ô∏è",
  duration: "15-30 seconds",
  style: "Quick fact format"
});

const tiktokContent = {
  platform: "tiktok",
  original_title: title,
  scripts: scripts,
  hashtags: "#fyp #viral #learning #tips #educational #ai #content",
  music_suggestions: ["Trending upbeat track", "Educational background music", "Viral sound of the week"],
  visual_notes: "Use dynamic text overlays, quick cuts, engaging transitions"
};

return [{ json: tiktokContent }];

// üìù Blog Post Generator
const transcription = items[0].json.transcription.text;
const title = items[0].json.title;
const duration = Math.round(items[0].json.duration / 60);

// Create structured blog post
const sentences = transcription.split(/[.!?]+/);
const sections = [];
let currentSection = "";
const wordsPerSection = 100;

// Split into sections
for (let i = 0; i < sentences.length; i++) {
  const sentence = sentences[i].trim();
  if (!sentence) continue;

  currentSection += sentence + ". ";

  if (currentSection.split(' ').length >= wordsPerSection || i === sentences.length - 1) {
    sections.push(currentSection.trim());
    currentSection = "";
  }
}

// Generate headings for sections
const sectionTitles = [
  "Introduction",
  "Key Points", 
  "Main Insights",
  "Practical Applications",
  "Expert Opinion",
  "Conclusion"
];

const blogPost = {
  platform: "blog",
  title: `üì∫ ${title} - Complete Analysis & Summary`,
  meta_description: transcription.substring(0, 160),
  introduction: `In this comprehensive analysis of the ${duration}-minute video "${title}", we break down all the key insights and actionable takeaways.`,
  sections: sections.slice(0, 6).map((content, index) => ({
    heading: sectionTitles[index] || `Section ${index + 1}`,
    content: content,
    word_count: content.split(' ').length
  })),
  conclusion: "These insights from the video provide valuable perspective on the topic. Apply these concepts to see real results.",
  seo: {
    keywords: "video analysis, insights, learning, education",
    word_count: transcription.split(' ').length,
    reading_time: Math.ceil(transcription.split(' ').length / 200)
  },
  cta: "Watch the full video for complete context and additional details."
};

return [{ json: blogPost }];

// üìä Content Factory Summary
const data = items[0].json;

// Count generated content pieces
const contentCount = {
  twitter_tweets: data.twitter_thread.total_tweets || 0,
  linkedin_posts: 1,
  instagram_slides: data.instagram_content.carousel_slides.length || 0,
  tiktok_scripts: data.tiktok_scripts.scripts.length || 0,
  blog_sections: data.blog_post.sections.length || 0,
  audio_files: 1,
  images: 1
};

const totalPieces = Object.values(contentCount).reduce((sum, count) => sum + count, 0);

// Create summary
const summary = {
  factory_results: {
    total_content_pieces: totalPieces,
    breakdown: contentCount,
    original_video: data.original_video_title,
    processing_completed: data.processing_time
  },
  ready_to_use: {
    twitter: `${contentCount.twitter_tweets} tweets ready to schedule`,
    linkedin: "Professional post with engagement hooks", 
    instagram: `Carousel with ${contentCount.instagram_slides} slides + story version`,
    tiktok: `${contentCount.tiktok_scripts} different scripts for viral content`,
    blog: `SEO-optimized article with ${contentCount.blog_sections} sections`,
    podcast: "Audio summary for podcast intro/outro",
    thumbnail: "High-quality thumbnail for all platforms"
  },
  next_steps: [
    "Schedule Twitter thread with 2-hour intervals",
    "Post LinkedIn content during business hours", 
    "Create Instagram carousel design",
    "Record TikTok videos with provided scripts",
    "Publish blog post with SEO optimization",
    "Use audio for podcast or YouTube intro"
  ],
  estimated_reach: {
    twitter: "500-2000 impressions per tweet",
    linkedin: "1000-5000 professional views",
    instagram: "2000-10000 reach with good hashtags",
    tiktok: "10000-100000+ potential viral reach",
    blog: "Ongoing organic search traffic"
  }
};

// Combine with all content
const finalOutput = {
  ...data,
  summary: summary
};

return [{ json: finalOutput }];
