const data = items[0].json;
const transcription = data.transcription.text;
const title = data.title;
const duration = Math.round(data.duration / 60);

// Text in Paragraphen aufteilen
const sentences = transcription.split(/[.!?]+/);
const paragraphs = [];
let currentParagraph = "";

sentences.forEach(sentence => {
  if (currentParagraph.length + sentence.length < 300) {
    currentParagraph += sentence.trim() + ". ";
  } else {
    if (currentParagraph) paragraphs.push(currentParagraph.trim());
    currentParagraph = sentence.trim() + ". ";
  }
});

if (currentParagraph) paragraphs.push(currentParagraph.trim());

